pragma solidity 0.4.18;


contract EscrowContract {

    // Stage Management
    enum Stages {
        Funding,
        SetStrings,
        ApproveOrReject,
        PayoutAndReveal
    }

    Stages public stage = Stages.Funding;

    modifier isProvider() {
        assert(msg.sender == agreement.providerAddress);
        _;
    }

    modifier isClient() {
        assert(msg.sender == agreement.clientAddress);
        _;
    }

    modifier atStage(Stages _stage) {
        assert(stage == _stage);
        _;
    }

    modifier transitionNext() {
        _;
        nextStage();
    }

    // Agreement
    struct Agreement {
        uint256 agreementPrice;
        address clientAddress;
        address providerAddress;
        string previewMessage;
        string finalMessage;
        bool approved;
    }

    Agreement agreement;

    mapping (address => uint256) funders;


    // Contract
    function EscrowContract(uint256 _agreementPrice, address _clientAddress) public {
        agreement.agreementPrice = _agreementPrice;
        agreement.clientAddress = _clientAddress;
        agreement.providerAddress = msg.sender;
        agreement.approved = false;
    }

    // Funding
    function contributeFunding(uint256 _fundingAmount) public payable {
        funders[msg.sender] = _fundingAmount;
        if(this.balance >= agreement.agreementPrice) {
            nextStage();
        }
    }

    // Provider
    function setPreviewMessage(string _previewMessage) public atStage(Stages.SetStrings) isProvider {
        agreement.previewMessage = _previewMessage;
    }

    function setFinalMessage(string _finalMessage) public atStage(Stages.SetStrings) isProvider {
        agreement.finalMessage = _finalMessage;
    }

    function confirmMessagesSet() public atStage(Stages.SetStrings) isProvider {
        assert(bytes(agreement.previewMessage).length > 0 && bytes(agreement.finalMessage).length > 0);
        agreement.approved = true;
        nextStage();
    }

    // Client
    function getPreviewMessage() public view atStage(Stages.ApproveOrReject) isClient returns (string) {
        return agreement.previewMessage;
    }

    function approvePreview(bool _approved) atStage(Stages.ApproveOrReject) public isClient {
        agreement.approved = _approved;
        if (agreement.approved) {
            agreement.providerAddress.transfer(this.balance);
            nextStage();
        }
    }

    function getFinalMessage() public view atStage(Stages.PayoutAndReveal) isClient returns (string) {
        return agreement.finalMessage;
    }

    // Abstractions - Getters
    function getStage() public view returns(uint) {
        return uint(stage);
    }

    function getFundingAmount() public view returns(uint) {
        return this.balance;
    }

    // Destroy Contract and send remaining balance to provider
    function kill() public isProvider {
        selfdestruct(agreement.providerAddress);
    }

    // Internal
    function nextStage() internal {
        stage = Stages(uint(stage) + 1);
    }
}
